// Comprehensive Error Handling System
const winston = require('winston');
const Sentry = require('@sentry/node');
const { v4: uuidv4 } = require('uuid');

// Initialize Sentry for error tracking
Sentry.init({
  dsn: process.env.SENTRY_DSN,
  environment: process.env.NODE_ENV,
  tracesSampleRate: 1.0,
});

// Custom Error Classes
class ApplicationError extends Error {
  constructor(message, statusCode = 500, code = 'INTERNAL_ERROR', context = {}) {
    super(message);
    this.name = this.constructor.name;
    this.statusCode = statusCode;
    this.code = code;
    this.context = context;
    this.timestamp = new Date().toISOString();
    this.id = uuidv4();

    Error.captureStackTrace(this, this.constructor);
  }
}

class ValidationError extends ApplicationError {
  constructor(message, field = null, value = null) {
    super(message, 400, 'VALIDATION_ERROR', { field, value });
  }
}

class NotFoundError extends ApplicationError {
  constructor(resource = 'Resource') {
    super(`${resource} not found`, 404, 'NOT_FOUND', { resource });
  }
}

// Error Logger
class ErrorLogger {
  constructor() {
    this.logger = winston.createLogger({
      level: 'info',
      format: winston.format.combine(
        winston.format.timestamp(),
        winston.format.errors({ stack: true }),
        winston.format.json()
      ),
      transports: [
        new winston.transports.File({ 
          filename: 'logs/error.log', 
          level: 'error' 
        }),
        new winston.transports.Console()
      ]
    });
  }

  logError(error, context = {}) {
    const errorInfo = {
      id: error.id || uuidv4(),
      name: error.name,
      message: error.message,
      stack: error.stack,
      statusCode: error.statusCode || 500,
      code: error.code || 'UNKNOWN_ERROR',
      timestamp: error.timestamp || new Date().toISOString(),
      context: { ...error.context, ...context },
      environment: process.env.NODE_ENV
    };

    // Log to Winston
    this.logger.error('Application Error', errorInfo);

    // Send to Sentry for tracking
    Sentry.withScope((scope) => {
      scope.setTag('errorCode', errorInfo.code);
      scope.setLevel('error');
      scope.setContext('errorDetails', {
        id: errorInfo.id,
        statusCode: errorInfo.statusCode,
        context: errorInfo.context
      });
      Sentry.captureException(error);
    });

    return errorInfo.id;
  }
}

const errorLogger = new ErrorLogger();

// Express Error Handling Middleware
const errorHandler = (error, req, res, next) => {
  const requestContext = {
    requestId: req.requestId,
    method: req.method,
    url: req.url,
    userAgent: req.get('User-Agent'),
    ip: req.ip,
    userId: req.user?.id
  };

  const errorId = errorLogger.logError(error, requestContext);
  const statusCode = error.statusCode || 500;

  res.status(statusCode).json({
    error: {
      id: errorId,
      message: error.message,
      code: error.code || 'INTERNAL_ERROR',
      timestamp: new Date().toISOString()
    }
  });
};

module.exports = {
  ApplicationError,
  ValidationError,
  NotFoundError,
  errorLogger,
  errorHandler
};

// Health Monitoring System
const express = require('express');
const axios = require('axios');

class HealthMonitor {
  constructor() {
    this.checks = new Map();
    this.results = new Map();
  }

  registerCheck(name, checkFn, options = {}) {
    this.checks.set(name, {
      fn: checkFn,
      interval: options.interval || 30000,
      timeout: options.timeout || 5000,
      critical: options.critical || false,
      consecutive_failures: 0
    });
  }

  async runCheck(name) {
    const check = this.checks.get(name);
    if (!check) return;

    const startTime = Date.now();
    let result = {
      name,
      status: 'healthy',
      message: 'OK',
      duration: 0,
      timestamp: new Date().toISOString()
    };

    try {
      const checkResult = await Promise.race([
        check.fn(),
        new Promise((_, reject) => 
          setTimeout(() => reject(new Error('Timeout')), check.timeout)
        )
      ]);

      result.duration = Date.now() - startTime;
      result.details = checkResult || {};
      check.consecutive_failures = 0;

    } catch (error) {
      result.status = 'unhealthy';
      result.message = error.message;
      result.duration = Date.now() - startTime;
      check.consecutive_failures++;
    }

    this.results.set(name, result);
    return result;
  }

  getHealthStatus() {
    const results = Array.from(this.results.values());
    const unhealthy = results.filter(r => r.status === 'unhealthy').length;

    return {
      status: unhealthy === 0 ? 'healthy' : 'degraded',
      timestamp: new Date().toISOString(),
      checks: results,
      summary: {
        total: results.length,
        healthy: results.length - unhealthy,
        unhealthy
      }
    };
  }
}

// Health Check Functions
const healthChecks = {
