    // External API check
    const response = await axios.get('https://api.external.com/health', {
      timeout: 3000
    });
    return { status: response.status };
  }
};

// Initialize health monitor
const healthMonitor = new HealthMonitor();

// Register checks
healthMonitor.registerCheck('database', healthChecks.database, {
  critical: true,
  interval: 30000
});

healthMonitor.registerCheck('redis', healthChecks.redis, {
  critical: true,
  interval: 30000
});

healthMonitor.registerCheck('external-api', healthChecks.externalApi, {
  critical: false,
  interval: 60000
});

// Health endpoints
const app = express();

app.get('/health', (req, res) => {
  const health = healthMonitor.getHealthStatus();
  const statusCode = health.status === 'healthy' ? 200 : 503;
  res.status(statusCode).json(health);
});

module.exports = { healthMonitor, app };

// Performance Monitoring with Prometheus
const prometheus = require('prom-client');
const express = require('express');

// Create a Registry
const register = new prometheus.Registry();

// Metrics
const httpRequestDuration = new prometheus.Histogram({
  name: 'http_request_duration_seconds',
  help: 'Duration of HTTP requests in seconds',
  labelNames: ['method', 'route', 'status_code'],
  buckets: [0.1, 0.3, 0.5, 0.7, 1, 3, 5, 7, 10]
});

const httpRequestsTotal = new prometheus.Counter({
  name: 'http_requests_total',
  help: 'Total number of HTTP requests',
  labelNames: ['method', 'route', 'status_code']
});

const activeConnections = new prometheus.Gauge({
  name: 'active_connections',
  help: 'Number of active connections'
});

const memoryUsage = new prometheus.Gauge({
  name: 'memory_usage_bytes',
  help: 'Memory usage in bytes',
  labelNames: ['type']
});

// Register metrics
register.registerMetric(httpRequestDuration);
register.registerMetric(httpRequestsTotal);
register.registerMetric(activeConnections);
register.registerMetric(memoryUsage);

// Performance middleware
const performanceMiddleware = (req, res, next) => {
  const startTime = Date.now();

  // Track active connections
  activeConnections.inc();

  res.on('finish', () => {
    const duration = (Date.now() - startTime) / 1000;
    const route = req.route ? req.route.path : req.path;

    // Record metrics
    httpRequestDuration
      .labels(req.method, route, res.statusCode)
      .observe(duration);

    httpRequestsTotal
      .labels(req.method, route, res.statusCode)
      .inc();

    activeConnections.dec();
  });

  next();
};

// Memory monitoring
setInterval(() => {
  const usage = process.memoryUsage();
  memoryUsage.labels('rss').set(usage.rss);
  memoryUsage.labels('heapTotal').set(usage.heapTotal);
  memoryUsage.labels('heapUsed').set(usage.heapUsed);
  memoryUsage.labels('external').set(usage.external);
}, 10000);

// Metrics endpoint
const app = express();
app.use(performanceMiddleware);

app.get('/metrics', async (req, res) => {
  res.set('Content-Type', register.contentType);
  res.end(await register.metrics());
});

module.exports = { app, performanceMiddleware };

// User Experience Monitoring
class UXMonitor {
  constructor() {
    this.events = [];
    this.sessionId = this.generateSessionId();
    this.userId = null;
  }

  generateSessionId() {
    return 'session_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
  }

  setUserId(userId) {
    this.userId = userId;
  }

  trackPageLoad(page, loadTime) {
    this.track('page_load', {
      page,
      loadTime,
      timestamp: Date.now()
    });
  }

  trackUserAction(action, element, context = {}) {
    this.track('user_action', {
      action,
      element,
      context,
      timestamp: Date.now()
    });
  }

  trackError(error, context = {}) {
    this.track('error', {
      message: error.message,
      stack: error.stack,
      context,
      timestamp: Date.now()
    });
  }

  track(eventType, eventData) {
    const event = {
      type: eventType,
      sessionId: this.sessionId,
      userId: this.userId,
      data: eventData,
      userAgent: navigator.userAgent,
      url: window.location.href,
      timestamp: Date.now()
    };

    this.events.push(event);
    this.sendEvent(event);
  }

  async sendEvent(event) {
    try {
      await fetch('/api/analytics/events', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(event)
      });
    } catch (error) {
      console.warn('Failed to send analytics event:', error);
    }
  }

  // Performance observer for Core Web Vitals
  observeWebVitals() {
    // Largest Contentful Paint (LCP)
    new PerformanceObserver((entryList) => {
      const entries = entryList.getEntries();
      const lastEntry = entries[entries.length - 1];
      this.track('web_vital', {
        name: 'LCP',
        value: lastEntry.startTime,
        timestamp: Date.now()
      });
    }).observe({ entryTypes: ['largest-contentful-paint'] });

    // First Input Delay (FID)
    new PerformanceObserver((entryList) => {
      const entries = entryList.getEntries();
      entries.forEach(entry => {
        this.track('web_vital', {
          name: 'FID',
          value: entry.processingStart - entry.startTime,
          timestamp: Date.now()
        });
      });
    }).observe({ entryTypes: ['first-input'] });

    // Cumulative Layout Shift (CLS)
    let clsValue = 0;
    new PerformanceObserver((entryList) => {
      const entries = entryList.getEntries();
      entries.forEach(entry => {
        if (!entry.hadRecentInput) {
          clsValue += entry.value;
        }
      });
      this.track('web_vital', {
        name: 'CLS',
        value: clsValue,
        timestamp: Date.now()
      });
    }).observe({ entryTypes: ['layout-shift'] });
  }
}

// Initialize UX monitoring
const uxMonitor = new UXMonitor();

// Auto-track page loads
window.addEventListener('load', () => {
  const loadTime = performance.timing.loadEventEnd - performance.timing.navigationStart;
  uxMonitor.trackPageLoad(window.location.pathname, loadTime);
});

// Auto-track errors
window.addEventListener('error', (event) => {
  uxMonitor.trackError(event.error, {
    filename: event.filename,
    lineno: event.lineno,
    colno: event.colno
  });
});

// Start observing web vitals
uxMonitor.observeWebVitals();

module.exports = { UXMonitor, uxMonitor };

// Incident Management System
class IncidentManager {
  constructor() {
    this.incidents = new Map();
    this.escalationRules = new Map();
    this.notificationChannels = new Map();
  }

  createIncident(alert, severity = 'medium') {
    const incident = {
      id: this.generateIncidentId(),
      title: alert.title || 'System Alert',
      description: alert.message,
      severity,
      status: 'open',
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
      assignedTo: null,
      escalationLevel: 0,
      timeline: [{
        timestamp: new Date().toISOString(),
        action: 'created',
        details: 'Incident created from alert',
        author: 'system'
      }],
      metadata: alert.metadata || {}
    };

    this.incidents.set(incident.id, incident);
    this.notifyIncident(incident);

    // Start escalation timer for high severity
    if (severity === 'high' || severity === 'critical') {
      this.startEscalationTimer(incident.id);
    }

    return incident;
  }

  updateIncident(incidentId, updates, author = 'system') {
    const incident = this.incidents.get(incidentId);
    if (!incident) return null;

    const previousStatus = incident.status;
    Object.assign(incident, updates);
    incident.updatedAt = new Date().toISOString();

    // Add timeline entry
    incident.timeline.push({
      timestamp: new Date().toISOString(),
      action: 'updated',
      details: `Status changed from ${previousStatus} to ${incident.status}`,
      author,
      changes: updates
    });

    return incident;
  }

  resolveIncident(incidentId, resolution, author) {
    const incident = this.updateIncident(incidentId, {
      status: 'resolved',
      resolvedAt: new Date().toISOString(),
      resolution
    }, author);

    if (incident) {
      this.schedulePostMortem(incident);
    }

    return incident;
  }

  escalateIncident(incidentId) {
    const incident = this.incidents.get(incidentId);
    if (!incident || incident.status === 'resolved') return;

    incident.escalationLevel++;
    incident.updatedAt = new Date().toISOString();

    incident.timeline.push({
      timestamp: new Date().toISOString(),
      action: 'escalated',
      details: `Escalated to level ${incident.escalationLevel}`,
      author: 'system'
    });

    // Notify escalation team
    this.notifyEscalation(incident);
  }

  async notifyIncident(incident) {
    // Send notifications to various channels
    const message = {
      title: `ðŸš¨ New Incident: ${incident.title}`,
      text: incident.description,
      severity: incident.severity,
      incidentId: incident.id,
      timestamp: incident.createdAt
    };

    // Send to Slack, email, SMS etc.
    await this.sendSlackNotification(message);
  }

  async sendSlackNotification(message) {
    const webhook = process.env.SLACK_INCIDENT_WEBHOOK;
    if (!webhook) return;

    try {
      await fetch(webhook, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          text: message.title,
          attachments: [{
            color: this.getSeverityColor(message.severity),
            fields: [
              { title: 'Severity', value: message.severity, short: true },
              { title: 'Incident ID', value: message.incidentId, short: true },
              { title: 'Description', value: message.text, short: false }
            ],
            timestamp: message.timestamp
          }]
        })
      });
    } catch (error) {
      console.error('Failed to send Slack notification:', error);
    }
  }

  getSeverityColor(severity) {
    const colors = {
      'low': 'good',
      'medium': 'warning',
      'high': 'danger',
      'critical': '#ff0000'
    };
    return colors[severity] || 'warning';
  }

  generateIncidentId() {
    const timestamp = Date.now().toString(36);
    const random = Math.random().toString(36).substr(2, 5);
    return `INC-${timestamp}-${random}`.toUpperCase();
  }
}

// Initialize incident manager
const incidentManager = new IncidentManager();

module.exports = { IncidentManager, incidentManager };
